import React, { useState } from "react";

const HomePage = () => {
  const [showForm, setShowForm] = useState(false);

  const handleShowForm = () => {
    setShowForm(true);
  };

  return (
    <div className="HomePage">
      <h1>Welcome to the color factory</h1>

      {showForm ? (
        // Form is visible
        <div>
          {/* Your form JSX goes here */}
          <form>
            {/* Form input fields and submit button go here */}
          </form>

          {/* You may also include a button to hide the form */}
          <button onClick={() => setShowForm(false)}>Hide Form</button>
        </div>
      ) : (
        // Form is hidden
        <button id="form-show-btn" onClick={handleShowForm}>
          Add a Color
        </button>
      )}
    </div>
  );
};

export default HomePage;
