import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Colors } from "./HomePage";
import nearestColor from 'nearest-color';

const colorLookup = nearestColor.from(require('color-name-list'));

const ColorForm = () => {
  const [color, setColor] = useState("");
  const navigate = useNavigate();

  const addColor = (color) => {
    Colors.push(color);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Map hex code to color name
    const colorName = colorLookup(color)?.name || "Color not found";

    // Add color name to the Colors array
    addColor(colorName);

    setColor("");
    // Use navigate to redirect to '/Colors'
    navigate("/Colors");
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="colorInput">Color:</label>
      <input
        type="color"
        id="colorInput"
        name="color"
        value={color}
        onChange={(e) => setColor(e.target.value)}
      />
      <button type="submit">Add Color</button>
    </form>
  );
};

export default ColorForm;
