import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Colors } from "./HomePage";
const colorList = require('color-name-list').colorList; // Access the colorList property

const ColorForm = () => {
  const [color, setColor] = useState("");
  const navigate = useNavigate();

  function hexToColorName(hexCode) {
    const colorNameEntry = colorList.find(entry => entry.hex === hexCode.toLowerCase());

    if (colorNameEntry) {
      return colorNameEntry.name;
    } else {
      return "Color not found";
    }
  }

  const addColor = (color) => {
    Colors.push(color);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addColor(hexToColorName(color));
    setColor("");
    // Use navigate to redirect to '/Colors'
    navigate("/Colors");
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="colorInput">Color:</label>
      <input
        type="color"
        id="colorInput"
        name="color"
        value={color}
        onChange={(e) => setColor(e.target.value)}
      />
      <button type="submit">Add Color</button>
    </form>
  );
};

export default ColorForm;
