// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ColorList from './ColorList';
import ColorDetail from './ColorDetail';
import NewColorForm from './NewColorForm';

const App = () => {
  const [colors, setColors] = useState([
    { id: '1', name: 'Red', hex: '#FF0000' },
    { id: '2', name: 'Green', hex: '#00FF00' },
    // ... other colors
  ]);

  const addColor = newColor => {
    setColors([newColor, ...colors]);
  };

  const redirectToColors = () => {
    // Redirect logic here
    // You can use react-router's useHistory or another navigation mechanism
    // Example using window.location
    window.location.href = '/colors';
  };

  return (
    <Router>
      <Routes>
        <Route path="/colors" element={<ColorList colors={colors} />} />
        <Route path="/colors/new" element={<ColorForm addColor={addColor} redirectToColors={redirectToColors} />} />
        <Route path="/colors/:colorId" element={<ColorDetail colors={colors} />} />
        {/* Redirect is replaced by Navigate in v6 */}
        <Route path="/" element={<Navigate to="/colors" />} />
      </Routes>
    </Router>
  );
};

export default App;
