// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import ColorList from './ColorList';
import ColorDetail from './ColorDetail';
import ColorForm from './ColorForm';

const App = () => {
  const [colors, setColors] = useState([
    { id: '1', name: 'Red', hex: '#FF0000' },
    { id: '2', name: 'Green', hex: '#00FF00' },
    // ... other colors
  ]);

  const addColor = newColor => {
    setColors([newColor, ...colors]);
  };

  const redirectToColors = () => {
    // Redirect logic here
    // You can use react-router's useHistory or another navigation mechanism
    // Example using window.location
    window.location.href = '/colors';
  };

  return (
    <Router>
      <Route path="/colors" exact component={() => <ColorList colors={colors} />} />
      <Route
        path="/colors/new"
        component={() => <ColorForm addColor={addColor} redirectToColors={redirectToColors} />}
      />
      <Route path="/colors/:colorId" component={() => <ColorDetail colors={colors} />} />
      <Redirect from="/" to="/colors" />
    </Router>
  );
};

export default App;
