import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Color.css";

function Color({ hex, color }) {
  const navigate = useNavigate();

  useEffect(() => {
    // Check if hex is not available and navigate to "/colors"
    if (!hex) {
      navigate("/colors");
    }
    // Add hex as a dependency if needed, but be cautious about potential infinite loops
  }, [hex, navigate]);

  // Render the component as usual
  return (
    <div className="Color" style={{ backgroundColor: hex }}>
      <p>this is {color}.</p>
      <p>Isn't it beautiful?</p>
      <p>
        <Link to="/">Go back</Link>
      </p>
    </div>
  );
}

export default Color;
