import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import ColorForm from "./ColorForm";
import colorNameList from "color-name-list";

const HomePage = () => {
  const [colors, setColors] = useState([]);

  const addColor = (hexCode) => {
    const colorName = getColorName(hexCode);
    setColors([...colors, colorName]);
  };

  const getColorName = (hexCode) => {
    const colorInfo = colorNameList.find((color) => color.hex === hexCode);
    return colorInfo ? colorInfo.name : "Unknown Color";
  };

  return (
    <div className="HomePage">
      <h1>Welcome to the color factory</h1>

      <button>
        <NavLink to="/ColorForm">Add a Color </NavLink>
      </button>

      {/* Display the list of colors */}
      <div>
        <h2>Colors:</h2>
        <ul>
          {colors.map((color, index) => (
            <li key={index}>{color}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HomePage;
