import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {Colors} from "./HomePage";

const ColorForm = () => {
    const [color, setColor] = useState("");
    const navigate = useNavigate();
    const colorList = require('color-name-list');

    // function hexToColorName(hexCode) {
    //     const colorNameEntry = colorList.find(entry => entry.hex === hexCode.toLowerCase());

    //     if (colorNameEntry) {
    //         return colorNameEntry.name;
    //     } else {
    //         return "Color not found";
    //     }

    // }
    const colorName = require('color-name');
    const ColorThief = require('color-thief');
    const colorThief = new ColorThief();
    function getColorName(rgb) {
        const [r, g, b] = rgb;
        const closestColor = colorName.closest(r, g, b);

        if (closestColor) {
          return closestColor.name;
        } else {
          return "Color not found";
        }
      }
    function rgbToColorName(rgb) {
        const colorThief = new ColorThief();
        const dominantColor = colorThief.getColor(rgb);

        // You can then use a color naming library or API to get the color name.
        // This example uses a hypothetical function 'getColorName' which you should replace with a proper implementation.
        const colorName = getColorName(dominantColor);

        return colorName;
      }



    const addColor = (color) =>{
        Colors.push(color);
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        addColor(rgbToColorName(color));
        setColor("");
        // Use navigate to redirect to '/Colors'
        navigate("/Colors");
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="colorInput">Color:</label>
      <input
        type="color"
        id="colorInput"
        name="color"
        value={color}
        onChange={(e) => setColor(e.target.value)}
      />
      <button type="submit">Add Color</button>
    </form>
  );
};

export default ColorForm;
