import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import ColorForm from "./ColorForm";
import nearestColor from 'nearest-color';

const colorLookup = nearestColor.from(require('color-name-list'));

const getColorName = (hexCode) => {
  const color = colorLookup(hexCode);
  return color ? color.name : "Color not found";
};

const HomePage = () => {
  const [colors, setColors] = useState([]);

  return (
    <div className="HomePage">
      <h1>Welcome to the color factory</h1>

      <button>
        <NavLink to="/ColorForm">Add a Color </NavLink>
      </button>

      {/* Display the list of colors */}
      <div>
        <h2>Colors:</h2>
        <ul>
          {colors.map((hexCode, index) => (
            <li key={index}>{hexCode && getColorName(hexCode)}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export const Colors = [];
export default HomePage;
