// ColorDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';

const ColorDetail = ({ colors }) => {
  const { color } = useParams(); // Change from colorId to color
  const selectedColor = colors.find(c => c.id === color);

  if (!selectedColor) {
    // Handle color not found (optional)
    return <div>Color not found</div>;
  }

  return (
    <div>
      <h1>{selectedColor.name}</h1>
      <div style={{ backgroundColor: selectedColor.hex, width: '100px', height: '100px' }}></div>
    </div>
  );
};

export default ColorDetail;
