import React, { useState } from "react";
import { NavLink, useParams } from "react-router-dom";
import ColorForm from "./ColorForm";
import colorNameList from "color-name-list";
import { v4 as uuid } from 'uuid';

const HomePage = () => {
  const INITIAL_STATE = [
    { id: uuid(), name: "red", qty: 'code1' },
    { id: uuid(), name: "green", qty: 'code2' }
  ];

  const { color, code } = useParams();
  const [colors, setColors] = useState(INITIAL_STATE);

  // Assuming you want to add a new color based on the params
  const handleAddColor = () => {
    const newColor = { id: uuid(), name: color, qty: code };
    setColors(prevColors => [...prevColors, newColor]);
  };

  return (
    <div className="HomePage">
      <h1>Welcome to the color factory</h1>

      <button>
        <NavLink to="/ColorForm">Add a Color</NavLink>
      </button>

      {/* Display the list of colors */}
      <div>
        <h2>Colors:</h2>
        <ul>
          {colors.map((color, index) => (
            <li key={index}>{color.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HomePage;
